import { createApp } from 'vue'
import App from './App.vue'

import router from './router';
//按需引入
import 'element3/lib/theme-chalk/button.css'
import {  ElTabs,
    ElTabPane,
    ElButton,
    ElInput,
    ElSelect,
    ElOption,
    setupGlobalOptions,
    ElImage,
    ElForm,
    ElFormItem,
    ElPopover,
    ElTable,
    ElTableColumn,
    ElPageHeader } from 'element3'

// 引入 echarts 核心模块，核心模块提供了 echarts 使用必须要的接口。
import * as echarts from 'echarts/core';
// 引入柱状图图表，图表后缀都为 Chart
import { BarChart } from 'echarts/charts';
// 引入标题，提示框，直角坐标系，数据集，内置数据转换器组件，组件后缀都为 Component
import {
  TitleComponent,
  TooltipComponent,
  GridComponent,
  DatasetComponent,
  TransformComponent
} from 'echarts/components';
// 标签自动布局、全局过渡动画等特性
import { LabelLayout, UniversalTransition } from 'echarts/features';
// 引入 Canvas 渲染器，注意引入 CanvasRenderer 或者 SVGRenderer 是必须的一步
import { CanvasRenderer } from 'echarts/renderers';

// 注册必须的组件
echarts.use([
  TitleComponent,
  TooltipComponent,
  GridComponent,
  DatasetComponent,
  TransformComponent,
  BarChart,
  LabelLayout,
  UniversalTransition,
  CanvasRenderer
]);



import * as API from "@/api"


const app = createApp(App);
app.use(router);
app.use(setupGlobalOptions({ size: 'small', zIndex: 3000 }));
app.use(ElTabs);
app.use(ElTabPane).use(ElButton).use(ElInput)
app.use(ElSelect).use(ElOption).use(ElImage).use(ElForm).use(ElFormItem).use(ElPopover)
app.use(ElTable).use(ElTableColumn).use(ElPageHeader)
// 添加全局属性
app.config.globalProperties.$API = API;

app.mount('#app');
